import { createGlobalStyle } from "styled-components";

const GlobalStyles = createGlobalStyle`
  width: 100vw;
  height: 100vh;
  padding: 200px 8%;
  box-sizing: border-box;
	body{
		margin-top: 10%;
		@media (max-width: 768px) {
			margin-top: 460px;
    }
		${'' /* @media (max-width: 1024px) {
			margin-top: 460px;
    } */}
	}
`;



export default GlobalStyles;
