import React, { Component } from "react";
import styled from "styled-components";
import { MyContext } from "../context";
import { Link } from "react-router-dom";

const LogInTag = styled.form`
  background-image: url("/images/oval-bg.png");
  background-repeat: no-repeat;
  background-size: cover;
  width: 100vw;
  height: 100vh;
  padding: 6% 10%;
  text-align: left;
  box-sizing: border-box;
  display: flex;
  flex-direction: row;
  p {
    font-size: 1.2rem;
  }
  .duo-div {
    width: 40%;
  }
  .duo-div:last-of-type {
    padding-left: 20%;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    button {
      width: 100%;
      height: 60px;
      font-size: 1.3rem;
      font-weight: 600;
      border: none;
      border-radius: 8px;
    }
    h3 {
      font-size: 2rem;
      font-weight: 300;
      margin: 5px 0;
    }
    h2 {
      font-size: 2.4rem;
      margin: 0;
    }
  }
  .form-container {
    display: flex;
    flex-direction: column;
    margin: 40px 0;
    tag {
      color: #949494;
    }
    input {
      padding: 14px;
      border: none;
      background: #ececec;
      margin: 5px 0;
      width: 110%;
    }
  }
`;

export default class LogIn extends Component {
  componentDidMount() {
    if (this.context.loggedUser) {
      return this.props.history.push("/profile");
    }
  }
  render() {
    return (
      <MyContext.Consumer>
        {context => (
          <LogInTag
            onSubmit={e => {
              context.handleLogin(e, () => {
                this.props.history.push("/profile");
              });
            }}
          >
            <div className="duo-div">
              <h1>Log In</h1>
              <div className="form-container">
                <tag>Username</tag>
                <input
                  type="text"
                  name="username"
                  onChange={e => context.handleInput(e, "loginForm")}
                  value={context.loginForm.username}
                />
              </div>

              <div className="form-container">
                <tag>Password</tag>
                <input
                  type="password"
                  name="password"
                  onChange={e => context.handleInput(e, "loginForm")}
                  value={context.loginForm.password}
                />
              </div>
              <p>
                If you don't jave an account yet, you can create your account{" "}
                <Link to="/signup">here</Link>
              </p>
            </div>
            <div className="duo-div">
              <div>
                <h2>Hello!</h2>
                <h3>Awesome to have you at IronProfile again!</h3>
              </div>
              <div>
                <p>
                  If you signup, you agree with all our terms and conditions
                  where we can do whatever we want with the data!
                </p>
                <button type="submit">Log In</button>
              </div>
            </div>
          </LogInTag>
        )}
      </MyContext.Consumer>
    );
  }
}
LogIn.contextType = MyContext;
