export default function Collapsible(props) {
  return (
    <MyContext.Consumer>
      {context => (
        <StyledCollapsible open = {open}>
          <button onClick={context.toggleMenu}>X</button>
          {
            (props.navbar.user) 
            ? (
              <>
              <StyleLink to="/profile">Profile</StyleLink>
              <StyleLink to="/orders">My Orders</StyleLink>
              <StyleLink to="/profile-address">Adresses</StyleLink>
              <p onClik = {context.handleLogout(()=>props.history.push("/"))}>Logout</p>
              </>
              )
            : (navbar.heart) 
            ? (
              <>
              <h2>WishList</h2>
              <div className = "wishlist-elements-container">
              {context.wishListProds.map(e=>(
                <h2>{e.name}</h2>
              ))}
              </div> 
              </>
              ) 
            : (navbar.shoppingcart)
            ? (
                <>
                <h2>Your Cart</h2>
                <div className = "cart-products">
                {context.Cart.map(e=>(
                  <p>{e.name} : {e.price}</p>
                ))}
                <p>Total: {context.Cart.map(e => ({}))} </p>
                </div>
                </>
              )
            : null
          }
        </StyledCollapsible>
      )}
    </MyContext.Consumer>
  );
}
