import React, {
  Component,
  createContext
} from "react";
import MY_SERVICE from "./services/index";
import Swal from "sweetalert2";

export const MyContext = createContext();

class MyProvider extends Component {
  state = {
    loggedUser: false,
    formSignup: {
      username: "",
      campus: "",
      password: "",
      course: ""
    },
    loginForm: {
      username: "",
      password: ""
    },
    stockInputs: [0, 0],
    user: {},
    NewProduct: {
      name: "",
      brand: "",
      caption: "",
      price: 0,
      color: [],
      size: [],
      quantity: [],
      details: "",
      image: [],
      category: "",
      subcategory: ""
    }
  };
  componentDidMount() {
    if (document.cookie) {
      MY_SERVICE.getUser()
        .then(({
          data
        }) => {
          this.setState({
            loggedUser: true,
            user: data.user
          });
          Swal.fire(`Welcome back ${data.user.name} `, "", "success");
        })
        .catch(err => console.log(err));
    }
  }

  handleInput = (e, obj) => {
    const a = this.state[obj];
    const key = e.target.name;
    a[key] = e.target.value;
    this.setState({
      obj: a
    });
  };

  handleSignup = async e => {
    e.preventDefault();
    const {
      data
    } = await MY_SERVICE.signup(this.state.formSignup);
    Swal.fire(`Welcome ${data.user.name}`, "User created", "success");
  };

  handleStockInput = e => {
    const a = e.target.name;
    const list = this.state.NewProduct.a;
    list.push(e.target.value);
    const addStock = {}
    addStock[a] = list
    this.setState({
      NewProduct: addStock
    })
  }

  addProduct = async e => {
    e.preventDefault();
    const {
      data
    } = await MY_SERVICE.addproduct(this.state.NewProduct)
    Swal.fire(`Success`, `New Product Created ${data.product.name}`, "success")
  }

  addStockForm = e => {
    e.preventDefault();
    const {stockInputs} = this.state
    this.setState(state => {
      const list = this.state.stockInputs.push(0);
      return {
        stockInputs: list
      };
    })
  }


  handleLogin = (e, cb) => {
    e.preventDefault();
    MY_SERVICE.login(this.state.loginForm)
      .then(({
        data
      }) => {
        console.log(data)
        this.setState({
          loggedUser: true,
          user: data.user
        })
        cb()
      })
      .catch(err => {
        Swal.fire(`Quien sabe que paso`, '☠️', 'error')
      });
  };

  handleLogout = async cb => {
    await MY_SERVICE.logout();

    window.localStorage.clear();
    this.setState({
      loggedUser: false,
      user: {}
    });
    cb();
  };

  render() {
    return ( <MyContext.Provider value = {
        {
          loggedUser: this.state.loggedUser,
          formSignup: this.state.formSignup,
          loginForm: this.state.loginForm,
          handleInput: this.handleInput,
          handleSignup: this.handleSignup,
          handleLogin: this.handleLogin,
          handleLogout: this.handleLogout,
          // user: this.state.user,
          state: this.state
        }
      } >
      {
        this.props.children
      } 
      </MyContext.Provider>
    );
  }
}

export default MyProvider;
