import React, { Component } from "react";
import Collapsible from "../components/collapsibles/Collapsible";
import { MyContext } from "../context";
import { NavMain, BtnTransparent } from "../styledComponents/StyledComps";
import * as Icon from "react-feather";

class NavBar extends Component {
  state = {
    heart: {},
    user: {
      name: "info",
      name: "orden",
      name: "likes",
      name: "address",
      name: "logout"
    },
    shoppingCart: {
      name: "products"
    }
  };

  handleClick = (e, icon) => {
    console.log("evento ", e.target, a);
    if (icon === "heart") {
      this.setState({ heart: { name: "Likes" } });
    } else if (icon === "user") {
      this.setState({
        user: {
          name: "info",
          name: "orden",
          name: "likes",
          name: "address",
          name: "logout"
        }
      });
    } else if (icon === "shoppingcart") {
      this.setState({
        shoppingCart: {
          name: "products"
        }
      });
    }
  };
  render() {
    return (
      <MyContext.Consumer>
        {context => (
          <NavMain>
            <Collapsible items={[this.state]} open={context.open} />
            <h1 className="logo">MoMe</h1>
            <div className="main-nav-icons">
              <BtnTransparent
                iicon="1"
                onClick={e => {
                  this.handleClick(e, "heart");
                  context.toggleMenu(e);
                }}
              >
                <Icon.Heart icon={"heart"} size="24" color="white" />
              </BtnTransparent>

              <BtnTransparent
                onClick={e => {
                  this.handleClick(e, "user");
                  context.toggleMenu(e);
                }}
              >
                <Icon.User icon={"user"} size="24" color="white" />
              </BtnTransparent>

              <BtnTransparent
                onClick={e => {
                  this.handleClick(e, "shoppingcart");
                  context.toggleMenu(e);
                }}
              >
                <Icon.ShoppingCart
                  icon={"shoppingcart"}
                  size="24"
                  color="white"
                />
              </BtnTransparent>
            </div>
          </NavMain>
        )}
      </MyContext.Consumer>
    );
  }
}

export default NavBar;
