import React from "react";
import styled from "styled-components";
import { MyContext } from "../context";
import { Link } from "react-router-dom";
import { ProfileTag } from "../context";



export default function SignupContainer(props) {
    return (
        <MyContext.Consumer>
            {context => (
                <ProfileTag>
                    <div div className="duo-div">
                        <h1>Profile</h1>
                        <div className="form-container">
                            <tag>Username</tag>
                            <h3>{context.state.user.username}</h3>
                        </div>
                        <div className="form-container">
                            <tag>Campus</tag>
                            <h3>{context.state.user.campus}</h3>
                        </div>
                        <div className="form-container">
                            <tag>Course</tag>
                            <h3>{context.state.user.course}</h3>
                        </div>
                        <div>    
                            <button onClick={e => {
                                e.preventDefault()
                                context.handleLogout(() => {
                                  props.history.push("/login");
                                });
                            }}>Logout</button>
                        </div>
                    </div>
                    <div div className="duo-div">
                        <div>
                            {/* <img src={{}}></img> */}
                            <button type="submit">Edit Photo</button>
                        </div>
                        <div>
                            <p>
                                The user is able to upload a new profile photo, using
                                NodeJS and Multer upploader
                            </p>
                        </div>
                    </div>
                </ProfileTag>
            )}
        </MyContext.Consumer>
    );
}
