import React from "react";
import styled from "styled-components";
import { MyContext } from "../../context";

const StyledCollapsible = styled.section`
  padding: 20px;
  position: relative;
  background: white;
  ${"" /* position: fixed; */}
  position: absolute;
  height: 100%;
  top: 0;
  right: 0;
  width: 300px;
  height: 100vh;
  min-width: 400px;
  bottom: 0;
  box-shadow: 0 0 10px 3px rgba(0, 0, 0, 0.2);
  display: grid;
  grid-template-rows: auto 1fr auto;
  /* esto es la magia */
  z-index: 200;
  transform: translateX(100%);
  transition: all 0.3s;
  ${props => props.open && `transform: translateX(0);`};
`;
const StyledItem = styled.div``;

const Item = ({ title, body }) => {
  return <StyledItem>{title}</StyledItem>;
};

export default function Collapsible({ items, open }) {
  return (
    <MyContext.Consumer>
      {context => (
        <StyledCollapsible open={open}>
          <button onClick={context.toggleMenu}>X</button>
          {items.map(item => console.log(item))}
            <Item key={item.heart} {...item} />
        </StyledCollapsible>
      )}
    </MyContext.Consumer>
  );
}
