import React, { Component } from "react";
import { MyContext } from "../context"
import { thisTypeAnnotation } from "@babel/types";



export default class AddProducts extends Component {
	state = {
		stockInputs:[0,]
	}
	addStockForm(){
		this.setState(state => {
			const list = this.state.stockInputs.push(0);
			return {
				stockInputs:list
			};
	})
}
	render() {
		//EN STATE TIENE QUE SER UN ARRAY


    return (
      <MyContext.Consumer>
			{context=>(
				<div className="wrapper">
					<form onSubmit={context.addProduct}>
						<input type="text" name="name" placeholder="name"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="brand" placeholder="brand"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="caption" placeholder="caption"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="price" placeholder="price"  onChange={context.handleInput}/>
						<input type="text" name="size" placeholder="size"  onChange={context.handleInput}/>
						<input type="text" name="details" placeholder="details"  onChange={context.handleInput}/>
						<input type="text" name="category" placeholder="category"  onChange={context.handleInput}/>
						<input type="text" name="subcategory" placeholder="category"  onChange={context.handleInput}/>
						{this.state.stockInputs.map(()=>(<StockInput/>))}
						<button type="submit">Add Product</button>
					</form>
				</div>
				)}
			</MyContext.Consumer>);
  }
}

function StockInput(props){
	return(
		<MyContext.Consumer>
		{context=>(
			<>
		<input name="quantity" type="number" placeholder="0" onChange={e => context.handleStockInput(e)}/>
		<input name="color" type="text" placeholder="color"  onChange={e => context.handleStockInput(e)}/>
		<input name="size" type="text" placeholder="sizes"  onChange={e => context.handleStockInput(e)}/>
		<button onClick={context.addStockForm}>+</button>
		</>
		)}

		</MyContext.Consumer>
	)
}
