import React, { Component } from "react";
import Collapsible from "../components/collapsibles/Collapsible";
import { MyContext } from "../context";
import { NavMain, BtnTransparent } from "../styledComponents/StyledComps";
import * as Icon from "react-feather";

class NavBar extends Component {
  state = {
    heart: {
      name: "likedProducts"
    },
    user: {
      name: "info",
      name: "orden",
      name: "likes",
      name: "address",
      name: "logout"
    },
    shoppingCart: {
      name: "products"
    }
  };
  handleClick = (e, obj) => {
    console.log("evento ", e);
    const a = this.state[obj];
    const key = e.target.name;
    a[key] = e.target.value;
  };

  render() {
    return (
      <MyContext.Consumer>
        {context => (
          <NavMain>
            <Collapsible
              items={[
                {
                  heart: {},
                  user: {},
                  shoppingCart: {}
                }
              ]}
              open={context.open}
            />

            <h1 className="logo">MoMe</h1>
            <div className="main-nav-icons">
              <BtnTransparent
                onClick={context.toggleMenu}
              >
                <Icon.Heart icon={"heart"} size="24" color="white" />
              </BtnTransparent>
              <BtnTransparent
                onClick={context.toggleMenu}
                onChange={this.handleClick}
              >
                <Icon.User icon={"user"} size="24" color="white" />
              </BtnTransparent>
              <BtnTransparent
                onClick={context.toggleMenu}
                onChange={this.handleClick}
              >
                <Icon.ShoppingCart
                  icon={"shoppingcart"}
                  size="24"
                  color="white"
                />
              </BtnTransparent>
            </div>
          </NavMain>
        )}
      </MyContext.Consumer>
    );
  }
}

export default NavBar;
