import React, { Component } from "react";
import { MyContext } from "../../context";
import {
  CardProdWrap,
  Prodimg,
  SubHeaderHighC,
  H3a
} from "../../styledComponents/StyledComps";
import * as Icon from "react-feather";

function ProductCard(props) {
  return (
    <MyContext.Consumer>
      {context => (
        <CardProdWrap id={props.product._id}>
          <Prodimg>
            {/* <div className="heart-colored">
              <Icon.Heart size="24" color="black" />deleteProductFromWishlist
            </div> */}
            {context.wishListProds.includes(props.product._id)? (<button onClick={deleteProductFromWishlist}>RemoveToWishList</button>):(<button onClick = {context.addProductToWishlist}>AddToWishList</button>)}
            <img src="/images/sweat3.png" alt="mainphoto" />
          </Prodimg>
          <SubHeaderHighC>{props.product.brand}</SubHeaderHighC>
          <H3a>{props.product.name}</H3a>
          <H3a>
            <strong>${props.product.price}</strong>
          </H3a>
          {/* <small>
            <a href="#">more colors available</a>
          </small> */}
        </CardProdWrap>
      )}
    </MyContext.Consumer>
  );
}
export default ProductCard;
