import React from "react";
import { MyContext } from "../context";
import {
  SignUpTag,
  MainWrapperield,
  RowContainerRadio,
  CenterizeMe,
  RowContainer,
  SubHeaderHighC,
  H1a,
  TemplateBtn
} from "../styledComponents/StyledComps";

export default function CreateOrder(props) {
  return (
    <MyContext.Consumer>
      {context => (
          <MainWrapperield>
						<h2>Your Cart</h2>
              <div className="cart-products">
                {context.Cart.map((e,i) => (
                  <>
                    <p>{e.name}</p>
                    <p>Size:{e.size} </p>: 
										<button key={i} onClick = {e => {context.handleProductQtyOrder(e, false)}}>+</button>{e.quantity}<button key = {i} onClick = {context.handleProductQtyOrder(e, true)}>-</button>

                    <p>price: {(e.price*e.quantity)}</p>
                  </>
                ))}
							</div>
                <button onClick={context.submitOrder}>Submit Order</button>
                <p>Total: {context.totalValueCart}</p>
          </MainWrapperield>
      )}
    </MyContext.Consumer>
  );
}
