import React, { Component } from "react";
import { MyContext } from "../context"
import {addProdTag} from ".."


export default class AddProducts extends Component {

	render() {
		//EN STATE TIENE QUE SER UN ARRAY


    return (
      <MyContext.Consumer>
			{context=>(
				<addProdTag>
					<form onSubmit={context.addProduct}>
						<input type="text" name="name" placeholder="name"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="brand" placeholder="brand"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="caption" placeholder="caption"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="price" placeholder="price"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="size" placeholder="size"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="details" placeholder="details"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="category" placeholder="category"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						<input type="text" name="subcategory" placeholder="category"  onChange={e=> context.handleInput(e,"NewProduct")}/>
						{context.state.stockInputs.map(()=>(<StockInput/>))}
						<button onClick={context.addStockForm}>+</button>
						<button type="submit">Add Product</button>
					</form>
				</addProdTag>
				)}
			</MyContext.Consumer>);
  }
}

function StockInput(props){
	return(
		<MyContext.Consumer>
		{context=>(
			<>
		<input name="quantity" type="number" placeholder="0" onChange={context.handleStockInput}/>
		<input name="color" type="text" placeholder="color"  onChange={context.handleStockInput}/>
		<input name="size" type="text" placeholder="sizes"  onChange={context.handleStockInput}/>
		</>
		)}

		</MyContext.Consumer>
	)
}
